const reducer = (posts=[], action)=>{
    //  if(action.type === 'CREATE'){
    //      return state;
    //  }

    switch (action.type) {
        case 'FETCH_ALL':
            return posts;
        case 'CREATE':
            return posts;
        default:
            break;
    }

}
